using GameCatalog_V2.Core.Models;

namespace GameCatalog_V2.Core.Repository;

public class MockGamesRepository:IRepository 
{
    readonly List<GameModel> _games;
    readonly List<Genre> _genres;
    public MockGamesRepository()
    {
        _games = new List<GameModel>() 
        {
            new GameModel()
            {
                Id = 1,
                Name = "Gta VI",
                GenreId=2,
                ReleaseDate= new DateTime(2025,08,15),
                Image="data:image/jpeg;base64,"
            },

            new GameModel()
            {
                Id = 2,
                Name = "The Witcher IV",
                GenreId=1,
                ReleaseDate= new DateTime(2026,10,3),
                Image="data:image/jpeg;base64,"
            },
            new GameModel()
            {
                Id = 3,
                Name = "Pes 2025",
                GenreId=4,
                ReleaseDate= new DateTime(2025,10,8),
                Image="data:image/jpeg;base64,"
            },
        };
        _genres = new()
        {
            new()
            {
                Id=1,
                Name="Rpg"
            },
            
            new()
            {
                Id=2,
                Name="Simulator"
            },
            
            new()
            {
                Id=3,
                Name="Horror"
            },
            
            new()
            {
                Id=4,
                Name="Sport"
            }
        };
    }

    public bool EditGenre(Genre editedgenre)
    {
        var oldGenre = _genres.FirstOrDefault(x => x.Id.Equals(editedgenre.Id));
        if(oldGenre is not null)
        {
            oldGenre = editedgenre;
            return true;
        }
        else
          return false;
        
    }

    public List<GameModel> GetAllGames()
    {
        return _games;
    }

    public List<Genre> GetAllGenres()
    {
        return _genres;
    }

    public Genre GetGenreById(int id)
    {
        return _genres.FirstOrDefault(x => x.Id.Equals(id));
    }
}
